var data=response.tree.children;
//Looping in the tree - nested loops. the checking if leave exists is after the third level.
for(i=0;i<data.length;i++){
    if(typeof data[i].children != 'undefined') {
        for(j=0;j<data[i].children.length;j++){
            data[i].children[j].percentage=data[i].children[j].percentage*100;
            if(typeof data[i].children[j].children != 'undefined') {
                for(k=0;k<data[i].children[j].children.length;k++){
                    data[i].children[j].children[k].percentage=data[i].children[j].children[k].percentage*100;
                    if(typeof data[i].children[j].children[k].children != 'undefined') {
                        for(l=0;l<data[i].children[j].children[k].children.length;l++){
                            data[i].children[j].children[k].children[l].percentage=data[i].children[j].children[k].children[l].percentage*100;
                        }
                    }
                }
            }
        }
    }    
}

response.tree.data=data;
