// this line should not be removed
var dhatim_login = angular.module("dhatim_login", []);

// define all of the available AUTH EVENTS codes
dhatim_login
.constant('AUTH_EVENTS', {
    loginSuccess: 'auth-login-success',
    loginFailed: 'auth-login-failed',
    logoutSuccess: 'auth-logout-success',
    sessionTimeout: 'auth-session-timeout',
    notAuthenticated: 'auth-not-authenticated',
    notAuthorized: 'auth-not-authorized'
})

// service for Session ?
.service('Session', function () {
    this.create = function ( token, userName ) {
        this.userId = token;
        this.userName = userName;
    };
    this.destroy = function () {
        this.userId = null;
        this.userName = null;
    };
    return this;
})

// service for get query data
.factory('DataService', function() {
    var dataService = {};
    dataService.getDataQuery = function( queryName ) {
        var dq = new DataQuery(queryName);
        return dq.execute()
        .then(function() {
            var d = dq.getData();
            if( d.data ) {
                d = d.data;    
            }
            return d;
        });            
    };

    return dataService;
})  

// service for Auth
.factory('AuthService', function ( $http, Session ) {
    var authService = {};
    var decryptToken = function ( raw, pass ) {
        var token = '';
        try {
            token = CryptoJS.TripleDES
                .decrypt(raw, pass)
                .toString(CryptoJS.enc.Utf8);

        } catch (e) {}
        return token;
    };

    authService.login = function ( credentials ) {
        var postData = {
            tenantid : sessionStorage.dfx_tenantid,
            appid    : sessionStorage.dfx_appname,
            userid   : credentials.username,
            password : credentials.password
        };
        return $http
        .post('/app/login', postData)
        .then(function (answer) {
            rawToken = null;
            answer = answer.data;
            if ( answer.result && answer.result !== 'failed' ) {
                rawToken = answer.data;

            }

            return rawToken;
        })
        .then(function ( rawToken ) {
            var token = null;
            if(rawToken){
                token = rawToken.type === 'default'
                ? decryptToken(rawToken.token, credentials.password)
                : rawToken.token; // type === 'plain';
                try {
                    token = JSON.parse(token);
                    Session.create( token.id, credentials.username );
                    //console.log(Session);
                } catch(e) {}

            }

            return token;    
        });
    };

    authService.isAuthenticated = function () {
        return !!Session.userId;
    };

    return authService;
})

// controller
.controller( "dhatim_loginController", function( $scope, $rootScope, DataService, AUTH_EVENTS, AuthService ) {

    $scope.credentials = {
        username: '',
        password: ''
    };

    $scope.carrier = []; // array for all available carrier opertors	
    $scope.selectedItem = '';

    // get all available carrier opertors from dataQuery, use DataService
    DataService.getDataQuery('dhatim_login')
    .then(function(res) {
        var carrier = [];
        for(var i=0, len = res.length; i<len; i++) {
            carrier.push({
                'id' : res[i].id, 
                'title': res[i].name

            });

        }
        $scope.$apply(function() {
            $scope.carrier = carrier;
            if( $scope.carrier.length ){
                $scope.selectedItem = $scope.carrier[0].id;     
            }
        });    

    });

    $scope.cbChange = function() {
        //console.log('combobox changed!!!',$scope.selectedOperator);
    };

    // login function
    $scope.login = function (credentials) {

        AuthService.login(credentials).then(function (token) {
            carrier = {};
            $rootScope.$broadcast( AUTH_EVENTS.loginSuccess );
            //$scope.setCurrentUser(user);
        }, function () {
            $rootScope.$broadcast(AUTH_EVENTS.loginFailed);
        });

    };

});
