// service for get query data
.factory('DataService', function() {
    var dataService = {};
    dataService.getDataQuery = function( queryName ) {
        var dq = new DataQuery(queryName);
        return dq.execute()
        .then(function() {
            var d = dq.getData();
            if( d.data ) {
                d = d.data;    
            }
            return d;
        });            
    };

    return dataService;
})

// service for Auth
.factory('AuthService', function ( $http, $q, $window ) {
    var userInfo;
    var decryptToken = function ( raw, pass ) {
        var token = '';
        try {
            token = CryptoJS.TripleDES
                .decrypt(raw, pass)
                .toString(CryptoJS.enc.Utf8);

        } catch (e) {}
        return token;
    };
	/*
	 may be this function will change, 
	 now it's almost the same as we login in application
	*/
    function login(credentials) {
        var deferred = $q.defer();

        $http
		.post("/app/login", { 
			tenantid : $window.sessionStorage.dfx_tenantid,
			appid    : $window.sessionStorage.dfx_appname,
			userid   : credentials.username,
            password : credentials.password
		})
        .then(function (answer) {
			var accessToken = null, token = null;
			answer = answer.data;
			if ( !answer.result ) {
                return deferred.reject('server error');
            }
            if ( answer.result === 'failed' ) {
                return deferred.reject('unknown user or wrong password');
            }
            if( !answer.data ){
                return deferred.reject('unknown data');
            }
			accessToken = answer.data;	
		    token = accessToken.type === 'default'
                    ? decryptToken(accessToken.token, credentials.password)
	                : accessToken.token; // type === 'plain';

			try {
			    token = JSON.parse(token);
			    userInfo = {
                    accessToken: token.id,
                    userName: credentials.username
                };
                $window.sessionStorage.userInfo = JSON.stringify(userInfo);
                return deferred.resolve(userInfo);
			} catch(e) {
			    return deferred.reject(e);
			}

        }, function (error) {
            return deferred.reject(error);
        });

        return deferred.promise;
    }
    function getUserInfo() {
        return userInfo;
    }
    function setOperatorInfo(id, name) {
        $window.sessionStorage.carrierOperator = {'id':id, 'name':name};
    }
    function init() {
        if ($window.sessionStorage.userInfo) {
            userInfo = JSON.parse($window.sessionStorage["userInfo"]);
        }
    }
    init();
    return {
        login: login,
        getUserInfo: getUserInfo,
        setOperatorInfo: setOperatorInfo
    };
})

// controller
.controller( "dhatim_loginController", function( $scope, DataService, AuthService, $location ) {
    $scope.userInfo = null; // will get after login
    $scope.credentials = {
        username: '',
        password: ''
    };

    $scope.carrier = []; // array for all available carrier opertors	
    $scope.selectedItem = ''; // selected carrier operator

    // get all available carrier opertors from dataQuery, use DataService
    DataService.getDataQuery('dhatim_login')
    .then(function(res) {
        var carrier = [];
        for(var i=0, len = res.length; i<len; i++) {
            carrier.push({
                'id' : res[i].id, 
                'title': res[i].name

            });

        }
        $scope.$apply(function() {
            $scope.carrier = carrier; // array of carrier opertors
            if( $scope.carrier.length ){
				// selected id carrier operator
                $scope.selectedItem = $scope.carrier[0].id; 
                AuthService.setOperatorInfo($scope.carrier[0].id, $scope.carrier[0].title);
            }
        });    

    });

    $scope.cbChange = function() {
