// this line should not be removed
var wApprove = angular.module("wApprove",[]);

wApprove.controller( "wApproveController", function( $scope ) {

    var dq = new DataQuery( 'qGetTaskDetails' );
	dq.setParameters( {"task_id": sessionStorage.task_id} );
    dq.execute().
        done( function() {
            $scope.$apply(function() {
                $scope.task_details = dq.getData().data.data.variables;
                console.log($scope.task_details);
            });
        });

	 $scope.approve = function() {
        $('#btn_approve').addClass('active');
        $('#btn_reject').removeClass('active');
        $scope.decision = "Approved";
        console.log($scope.decision);
    };

    $scope.reject = function() {
        $('#btn_approve').removeClass('active');
        $('#btn_reject').addClass('active');
        $scope.decision = "Rejected";
        console.log($scope.decision);
    };

     $scope.submitTask = function() {
        console.log($scope.decision);
        var dq_submit = new DataQuery( 'qGmRequisitionApproval' );
        dq_submit.setParameters( {"task_id": sessionStorage.task_id, "action":"finish", "decision": $scope.decision} );
        dq_submit.execute().
            done( function() {
                window.location = "Home.html";
            });
    };
});
