// this line should not be removed
var wListInAccrodion = angular.module("wListInAccrodion",[]);

wListInAccrodion.controller( "wListInAccrodionController", function( $scope ) {
    var dq = new DataQuery( 'qUsBoxOffice' );
dq.execute().
	done( function() {
		$scope.$apply(function() {
			$scope.accordion_data = dq.getData().movies;
		});
	});
});
