// this line should not be removed
var wPreExec = angular.module("wPreExec",[]);

wPreExec.controller( "wPreExecController", function( $scope ) {
    $scope.All = function(arg) {
        var dq = new DataQuery( 'qGetPreAll' );
dq.execute().
	done( function() {
		$scope.$apply(function() {
			$scope.results = dq.getData().data;
		});
	});
    };
     $scope.All();
	$scope.pre_exec = function(arg) {
	    var dq = new DataQuery( 'qPreExecGet' );
	    dq.setParameters({"languauge":$scope.lang});
	    dq.execute().
	done( function() {
		$scope.$apply(function() {
			$scope.results = dq.getData().data;
		});
	});
	};
	$scope.no_exec = function(arg) {
	    var dq = new DataQuery( 'qPreExecNo' );
	    dq.setParameters({"languauge":$scope.lang});
	    dq.execute().
	done( function() {
		$scope.$apply(function() {
			$scope.results = dq.getData().data;
		});
	});
	};
    $scope.showHelp = function(arg) {
        $.SmartMessageBox({
            title : "What is Pre Execution Filter  ?",
            content : "Pre-Execution filter is an optionin DataQuery that Allows user to check Query Parameters abd modify them before Query get executed, this helps to validate user input and handle the correction on server side instead of client side. For more info about this example click <a href='#'>Here</a>",
            buttons : '[Ok]'
        });
};

});
